create table tabla_prog(
subcentro varchar(10) NOT NULL,
maq varchar(6) NOT NULL,
fecha date NOT NULL,
elemento varchar(10) NOT NULL,
legajo_programador varchar(10) NOT NULL,
programado int NOT NULL,
primary key (subcentro,maq,fecha,elemento),
foreign key (maq)
references tabla_maq(maq),
foreign key (elemento)
references tabla_elemento(elemento),
foreign key (subcentro)
references tabla_subcentro(subcentro),
foreign key (legajo_programador)
references tabla_programadores(legajo_programador)
);

create table tabla_prod(
subcentro varchar(10) NOT NULL,
maq varchar(6) NOT NULL,
fecha date NOT NULL,
elemento varchar(10) NOT NULL,
legajo_operador varchar(10) NOT NULL,
producido int NOT NULL,
primary key (maq,fecha,elemento),
foreign key (maq)
references tabla_maq(maq),
foreign key (elemento)
references tabla_elemento(elemento),
foreign key (subcentro)
references tabla_subcentro(subcentro),
foreign key (legajo_operador)
references tabla_operadores(legajo_operador)
);

create table tabla_interrupciones(
subcentro varchar(10) NOT NULL,
maq varchar(6) NOT NULL,
fecha date NOT NULL,
cod_raiz int NOT NULL,
tiempo_int int NOT NULL,
primary key (subcentro,maq,fecha,cod_raiz),
foreign key (maq)
references tabla_maq(maq),
foreign key (cod_raiz)
references tabla_cod_interrupciones(cod_raiz),
foreign key (subcentro)
references tabla_subcentro(subcentro)
);















/*FUNCIONES*/
/*La función es pasarle los parámetros de producido y programado y que calcule el % de cumplimiento de programa.*/
DELIMITER //
CREATE FUNCTION cumplimiento_programa(producido INT,programado INT) RETURNS FLOAT
DETERMINISTIC
BEGIN
   DECLARE relacion_programado_producido FLOAT;
   SET relacion_programado_producido = ((producido/programado) * 100 );
   RETURN relacion_programado_producido;
END
//

/*Las próximas dos funciones obtienen valores de otra tabla, reemplazando a un JOIN*/
DELIMITER //
CREATE FUNCTION obtener_detalle_nombre(dato varchar(10)) RETURNS VARCHAR(24)
DETERMINISTIC
BEGIN
   DECLARE dato_devuelto VARCHAR(24);
   SET dato_devuelto = (SELECT nombre_operador FROM proyecto.tabla_operadores WHERE legajo_operador=dato);
   RETURN dato_devuelto;
END
// 

DELIMITER //
CREATE FUNCTION obtener_detalle_apellido(dato1 varchar(10)) RETURNS VARCHAR(24)
DETERMINISTIC
BEGIN
   DECLARE dato_devuelto1 VARCHAR(24);
   SET dato_devuelto1 = (SELECT apellido_operador FROM proyecto.tabla_operadores WHERE legajo_operador=dato1);
   RETURN dato_devuelto1;
END
// 

/*STORED PROCEDURES*/
/*El objetivo es pasarle al SP como parámetros los valores de un INSERT INTO para la creación de un nuevo registro en la tabla tabla_programadores, 
con la salvedad que si el legajo del programador ya existe, te devuelva la leyenda “Legajo ya existe”, caso contrario, el registro se ingresa correctamente.*/
DELIMITER //
CREATE PROCEDURE ingreso_registro(IN legajo varchar(10),IN nombre varchar(10),IN apellido varchar(10))
BEGIN
IF (SELECT count(legajo_programador) from tabla_programadores where legajo_programador=legajo)>0 THEN
   SELECT 'Legajo ya existe';
ELSE
END IF;
END
//

